import Footer from 'components/navigations/Footer'
import Navbar from 'components/navigations/Navbar'
import Layouts from 'hocs/layouts/Layouts'
import React, {useEffect, useState} from 'react'
import { Link } from 'react-router-dom'



const request = async (url) => {
  const res = await fetch(url)
  if (!res.ok) {
    throw new Error('Error en la solicitud');
  }
  const data = await res.json();
  return data
}

const Home = () => {
  const [productos, setProductos] = useState({});

  useEffect(() => {
    const fetchData = async (url) => {
      try{
        const data = await request(url);
        setProductos(data);
        console.log(data)
      }catch(error){
        console.log("Ah ocurrido un error")
      };
    };
    fetchData('http://127.0.0.1:8000/agencia/servicios/');
  }, []);

  return (
    <Layouts>
        <Navbar/>
        <div className='pt-28'>
            {
              productos['results']?.map((producto)=> (
                <>
                  <h1 className='text-black'>{producto.nombre}</h1>
                  <p className='text-black'>{producto.descripcion}</p>
                </>
              ))
            }
            { 
              productos['next']
              ?
                
                <button onClick={() => fetchData(productos['next'])}>
                  Siguiente
                </button>
              :''
            }
        </div>
        <Footer/>
    </Layouts>
  )
}

export default Home
